    p.signal = function(channel, value) {

        // store beat values from channel 1
        if (channel == 1){
            this.currentBeatValue = value;
        }

        // store volume values from channel 2
        if (channel == 2){
            this.currentVolume = value * 1.3;
        }
    };


module.exports = CrashAndBurnVisualiser;



